import React from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom'

export default class CardList extends React.Component {
    constructor(props){
        super(props)
        this.state = {cards:[]}
    }

    componentDidMount(){
        axios.get("http://localhost:3000/cards")
        .then(res=>{
            this.setState({cards: res.data})
        })
    }

    deleteCard = (id) => {
        axios.delete("http://localhost:300/cards/" + id)
    }

    render(){
        return(
            <div style={{background: "rgba(0, 0, 0, 0.85)"}} className="ui center aligned container">
                <div>
                    <Link to={"/cards/add"}>Add a Card</Link> 
                </div>

                <div className="ui list">
                    {this.state.cards.map(card=>
                    <div className="item">
                        <img className="ui avatar image" src="/images/avatar/small/jenny.jpg" />
                        <div className="content">
                        <div className="header" style={{color: "orange"}}>{card.name}</div>
                        <div className="description"><a><b><Link to={"/cards/edit"}>Edit</Link></b></a> | <a onClick={this.deleteCard(card._id)}>Delete</a></div>
                        </div>
                    </div>)}
                </div>
            </div>
        )
    }
}
