import React from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom'
import Announcments from './admin-panel.components/Announcments.admin';
import UserList from './admin-panel.components/CardList.admin';
import CardList from './admin-panel.components/CardList.admin';
import Scoreboard from './admin-panel.components/Scoreboard.admin';
import { Sidebar } from './admin-panel.components/Sidebar'

export default class AdminPanel extends React.Component {
    constructor(props){
        super(props)
        this.state = {cards:[]}
        this.deleteCard = this.deleteCard.bind(this)
        this.currentTarget = null;
    }

    componentDidMount(){
        axios.get("http://localhost:3000/cards")
        .then(response=>{
            this.setState({cards: response.data})
            console.log('login response: ')
            console.log(response)
            if (response.status === 200) {
                console.log(response.data.admin)
                // update App.js state
                this.props.updateUser({
                    loggedIn: true,
                    username: response.data.username,
                    admin: response.data.admin
                })
                // update the state to redirect to home
                
            }
        }).catch(error => {
            console.log('login error: ')
            console.log(error);
            
        })
    }

    deleteCard = (id) => {
        axios.delete("http://localhost:3000/cards/" + id)
        .then((res)=>console.log(res.data),  window.location = "/cards")
        .catch((err)=>console.log(`Sorry, the request could not be made. Error: ${err}`));
    }

    onChangeHandler(event){
        currentTarget === event.target.name
        this.setState({
			display: event.target.name
		})
    }

    render(){
        
        return(
            <div style={{background: "rgba(255, 255, 255, 0.85)"}} className="ui center aligned container">
                <div className="ui grid">
                    <Sidebar displayHandler={this.displayHandler} />
                    <div class="twelve wide stretched column">
                        <div class="ui segment">
                            {this.state.display === currentTarget ? <CardList /> : null}
                            {this.state.display === currentTarget ? <UserList /> : null}
                            {this.state.display === currentTarget ? <Announcments /> : null}
                            {this.state.display === currentTarget ? <Scoreboard /> : null}
                          
                            
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
