import React from 'react';
import { BrowserRouter as Router, Route } from 'react-router-dom';
import axios from 'axios'

import { Navbar } from './components/Navbar';
import Game from './Game'
import CardList from './components/CardList';
import AddCard from './components/AddCard';
import EditCard from './components/EditCard'
import { Landing } from './components/Landing'
import Register from './components/Register'
import Login from './components/Login'
export default class App extends React.Component {
  constructor(){
    super()
    axios.defaults.withCredentials = true;

    this.state = {loggedIn: false, username: null, admin: false};

    this.getUser = this.getUser.bind(this)
    this.componentDidMount = this.componentDidMount.bind(this)
    this.updateUser = this.updateUser.bind(this)
    this.handleLogout = this.handleLogout.bind(this)
  }

  componentDidMount() {
    this.getUser()
  }

  updateUser (userObject) {
    this.setState(userObject)
  }

  handleLogout () {
    if(!this.state.username && !this.state.loggedIn){
      return window.location = ("/login")
    } else {
      axios.post('http://localhost:3000/user/logout')
      .then(()=>{
        console.log("log out complete")
        window.location = "/"
      })
    } 
  }

  getUser() {
    axios.get('http://localhost:3000/user/').then(response => {
      console.log('Get user response: ')
      console.log(response.data)
      if (response.data.user) {
        console.log('Get User: There is a user saved in the server session: ')
        console.log(response.data.user)
        console.log("Admin or not " + response.data.user.admin)

        this.setState({
          loggedIn: true,
          username: response.data.user.username,
          admin: this.state.admin,
        })
      } else {
        console.log('Get user: no user');
        this.setState({
          loggedIn: false,
          username: null
        })
      }
    })
  }

  render() {
    return (
      <Router >
        <Navbar loggedIn={this.state.loggedIn} admin={this.state.admin} username={this.state.username} handleLogout={this.handleLogout} />
        <Route path={"/"} exact component={Landing} />
        <Route path={"/register"} component={Register} />
        <Route path={"/login"} render={()=><Login updateUser={this.updateUser} />} />
        <Route path={"/phonics"} exact component={Game} />
        <Route path={"/cards"} updateUser={this.updateUser} exact component={CardList} />
        <Route path={"/cards/add"} updateUser={this.updateUser} exact component={AddCard} />
        <Route path={"/cards/edit/:id"} updateUser={this.updateUser} exact component={EditCard} />
      </Router>
    );
  }
}
