import React from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom'

export default class CardList extends React.Component {
    constructor(props){
        super(props)
        this.props.updateUser = this.updateUser;
        this.state = {cards:[]}
        this.deleteCard = this.deleteCard.bind(this)
    }

    componentDidMount(){
        axios.get("http://localhost:3000/cards")
        .then(response=>{
            this.setState({cards: response.data})
            console.log('login response: ')
            console.log(response)
            if (response.status === 200) {
                // update App.js state
                this.props.updateUser({
                    loggedIn: true,
                    username: response.data.username,
                    admin: response.data.admin
                })
                // update the state to redirect to home
                
            }
        }).catch(error => {
            console.log('login error: ')
            console.log(error);
            
        })
    }

    deleteCard = (id) => {
        axios.delete("http://localhost:3000/cards/" + id)
        .then((res)=>console.log(res.data),  window.location = "/cards")
        .catch((err)=>console.log(`Sorry, the request could not be made. Error: ${err}`));
    }

    render(){
        return(
            <div style={{background: "rgba(0, 0, 0, 0.85)"}} className="ui center aligned container">
                <div>
                    <Link to={"/cards/add"}>Add a Card</Link> 
                </div>

                <div className="ui list">
                    {this.state.cards.map(card=>
                    <div className="item">
                        <img className="ui avatar image" src="/images/avatar/small/jenny.jpg" />
                        <div className="content">
                        <div className="header" style={{color: "orange"}}>{card.name}</div>
                        <div className="description"><a><b><Link to={"/cards/edit/" + card._id}>Edit</Link></b></a> | <a onClick={()=>{this.deleteCard(card._id)}}>Delete</a></div>
                        </div>
                    </div>)}
                </div>
            </div>
        )
    }
}
