import React from 'react';
import { Link } from 'react-router-dom'
import axios from 'axios'
import { DynamicListItem as ListItem } from './DynamicListItem'


export default class CardList extends React.Component {
    constructor(props){
        super(props)
        this.state = {cards:[]}
        this.deleteItem = this.deleteItem.bind(this)
    }

    componentDidMount(){
        axios.get("http://localhost:3000/cards")
        .then(response=>{
            this.setState({cards: response.data})
            console.log('login response: ')
            console.log(response)
            if (response.status === 200) {
                console.log(response.data.admin)
                // update App.js state
                this.props.updateUser({
                    loggedIn: true,
                    username: response.data.username,
                    admin: response.data.admin
                })
                // update the state to redirect to home
                
            }
        }).catch(error => {
            console.log('login error: ')
            console.log(error);
            
        })
    }

    deleteItem = (id) => {
        axios.delete("http://localhost:3000/cards/" + id)
        .then((res)=>console.log(res.data),  window.location = "/admin/cards")
        .catch((err)=>console.log(`Sorry, the request could not be made. Error: ${err}`));
    }

    render(){
        return (
                <div>
                    <div className="ui tabular menu">
                        <a className="item active">
                                All Cards
                        </a>
                        <Link className="item" to="/admin/cards/add">Add a Card</Link>
                        <a className="item">
                            All Decks
                        </a>
                        <a className="item">
                             Add a Deck
                        </a>
                </div>
                <div className="ui relaxed divided list">
                    {this.state.cards !== [] ? this.state.cards.map((card)=>(
                        <ListItem mappedItem={card} />
                    )) : null}
                </div>
            </div>
        )
    }
}
