import React from 'react';
import { shuffle } from './utils.js'
import Gameboard from './components/Gameboard'
import { wordList } from './wordList'
import axios from 'axios';
export default class Game extends React.Component {
    constructor(props){
        super(props)
        this.state = {alphabet: 0, dropzoneWord: '', displayNextRoundButton: false, displayDifficultWords: false}
        this.inSession = false;
        this.currentWordsIndexCounter = 0;
        this.handleInit = this.handleInit.bind(this)
        this.handleOnNextRound = this.handleOnNextRound.bind(this);
        this.handleOnFinalCheckCorrect = this.handleOnFinalCheckCorrect.bind(this);
    }

    getWords = async function() {
        this.res = await axios.get('http://localhost:4000/phonics/cards')
};

    loadWords (){
        this.getWords()
        
        this.currentWords = this.res.data;
        this.currentWord = this.currentWords[this.currentWordsIndexCounter].name;
        this.currentImg = this.currentWords[this.currentWordsIndexCounter].img;
        this.difficultLetters = [...this.currentWords[this.currentWordsIndexCounter].name];
        console.log(this.currentWords)
        console.log(this.currentImg)
        
    
    }

    setAlphabetData () {
        this.setState({alphabet: 
            shuffle(["a", "b", "c", "d", "e", "f", "g", "h", "i", "j", "k", "l", "m", "n", "o", "p", "q", "r", "s", "t", "u", "v", "w", "x", "y", "z"])})
    }

    setDropzoneData (){
        this.setState({dropzoneWord: [...this.currentWords[this.currentWordsIndexCounter].name]})
    }
   
    //-- Game Controller Logic --\\

    roundStart = () => {
        this.loadWords();
        this.setDropzoneData();
        this.setAlphabetData();
       
    }

    roundComplete = () => {
        this.currentWordsIndexCounter  = this.currentWordsIndexCounter  + 1
    }

    init = () => {
        this.lettersCorrectCounter = 0;
        this.roundStart();
        this.inSession = !this.inSession;
    }
    
    //-- React Handlers --\\
    handleInit = () => {
        this.init()
    }

    handleOnNextRound = () => {
        this.roundStart()
        this.setState((state)=>({displayNextRoundButton: !this.state.displayNextRoundButton}))
    }

    handleOnFinalCheckCorrect = () => {
        this.roundComplete ()
        this.setState((state)=>({displayNextRoundButton: !this.state.displayNextRoundButton}))
    }

    handleOnHelperClick = () => {
        this.setAlphabetData()
    }

    render(){
        if(this.inSession){
            return (
                <div>
                    <Gameboard 
                        inSession={this.inSession} 
                        dropzoneWord={this.state.dropzoneWord} 
                        displayNextRoundButton={this.state.displayNextRoundButton} 
                        difficultLetters={this.difficultLetters} 
                        handleInit={this.handleInit} 
                        handleOnFinalCheckCorrect={this.handleOnFinalCheckCorrect}
                        handleOnNextRound={this.handleOnNextRound} 
                        alphabet={this.state.alphabet}
                        handleOnHelperClick={this.handleOnHelperClick}
                        currentImg={this.currentImg}
                    />
                </div>
            )
        }
        return (
            <div>
                <Gameboard inSession={this.inSession} handleInit={this.handleInit} />
               Click the start Button to start. 
            </div>
        )
    }
}
